import  '../stylesheets/master.css';
import  '../stylesheets/SearchBar.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ResultsPage from '../pages/ResultsPage';
import SearchIcon from './SearchIcon';
import Route from './Route';

// TODO:
// Search bar not responsive on mobile devices (button disappears)
const SearchBar = ({ button, width }) => {
  const [term, setTerm] = useState('');
  const [results, setResults] = useState([]);

  useEffect(() => {
    const search = async () => {
      const { data } = await axios.post("http://localhost:8080/app/clubsearch", {
        key: term
      });

      setResults(data);
    }

    search();
  }, [term]);

  const generateResults = (event) => {
    console.log("im working!")
    // event.preventDefault();
    // window.history.pushState({}, '', '/search');
    //
    // const navEvent = new PopStateEvent('popstate');
    // window.dispatchEvent(navEvent);
  };


  const populateSearchBar = (btn) => {
    if(!btn) {
      return (
        <React.Fragment>
          <span className="ml-0 mr-2">
            <SearchIcon />
          </span>
          <input
            type="text"
            value={term}
            onChange={e => { setTerm(e.target.value) }}
          />
        </React.Fragment>
      );
    } else{
      return (
        <React.Fragment>
          <input
            className="ml-0"
            type="text"
            value={term}
            onChange={e => { setTerm(e.target.value) } }
          />
          <button className="btn btn-light mr-0" id="search-btn" type='Submit'>
            <SearchIcon />
          </button>
        </React.Fragment>
      );
    }
  };

  return (
    <form onSubmit={generateResults} className={button ? "form-inline d-flex" : ''}>
      <div
        className="search-bar d-flex flex-row align-items-center pl-3 pr-2"
        style={{ width: width }}
        spellCheck="false flex-fill"
      >
        <input
          className={button ? "ml-0" : ''}
          type="text"
          value={term}
          onChange={e => { setTerm(e.target.value) } }
        />
        {populateSearchBar(button)}
      </div>
      <Route path="/search">
        <ResultsPage results={results}/>
      </Route>
    </form>
  );
};

SearchBar.defaultProps = {
  width: 'auto',
  button: false
};

export default SearchBar;
