import React, { useState, useEffect } from 'react';
import axios from 'axios';
import SearchResult from '../components/SearchResult';
import ResultContainer from '../components/ResultContainer';
import ContainerView from '../components/ContainerView';
import Route from '../components/Route';
import ClubPage from './ClubPage';

const ResultsPage = ({ term, onTermChange }) => {
  const [results, setResults] = useState([]);

  useEffect(() => {
    let isCancelled = false;

    const search = async () => {
      const { data } = await axios.post("http://localhost:8080/app/clubsearch",
        {
          key: term
        }
      )

      if(!isCancelled){
        setResults(data);
      }
    }

    search();

    return () => {
      isCancelled = true;
    };
  }, [term]);

  const renderedResults = results.map((result) => {
    return (
      <React.Fragment>
        <Route path={`/club/${result.club_id}`}>
          <ClubPage onTermChange={onTermChange} id={result.club_id}/>
        </Route>
        <SearchResult
          key={result.club_id}
          id={result.club_id}
          name={result.username}
          description={result.desc}
        />
      </React.Fragment>
    );
  });

  return (
    <ContainerView search={true} onTermChange={onTermChange}>
      <div className="container">
      <p className="my-3" style={{textAlign:'center'}}>{`${results.length} results found`}</p>
      <ResultContainer>
        {renderedResults}
      </ResultContainer>
      </div>
    </ContainerView>
  );
};

export default ResultsPage;
