import React, { useState } from 'react';
import axios from 'axios';
import ContainerView from '../components/ContainerView';
import InputField from '../components/InputField';
import InputArea from '../components/InputArea';
import Link from '../components/Link';

const validateEmail = (email) => {
  return (/^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/.test(email));
}

const UserFields = ({ username, email, password, setUsername, setEmail, setPassword }) => {
  return (
    <React.Fragment>
      <InputField type="text" title="Name" onChange={e => setUsername(e)}/>
      {username && username.length <= 20 ?
      null : <p className="text-danger">Please enter a username.</p>}

      <InputField type="email" title="Email" onChange={e => setEmail(e)}/>
      {email && validateEmail(email) ?
      null : <p className="text-danger">Please enter a valid email.</p>}

      <InputField type="password" title="Password" onChange={e => setPassword(e)}/>
      {password.length ? null : <p className="text-danger">Please enter a valid email.</p>}
    </React.Fragment>
  );
};

const ClubFields = ({ setUsername, setEmail, setPassword, setDescription }) => {
  return (
    <React.Fragment>
      <InputField type="text" title="Club Name" onChange={e => setUsername(e)}/>
      <InputField type="email" title="Email" onChange={e => setEmail(e)}/>
      <InputField type="password" title="Password" onChange={e => setPassword(e)}/>
      <InputArea placeholder="Club Description" onChange={e => setDescription(e)}/>
    </React.Fragment>
  );
};

const SignupPage = () => {
  const [ role, setRole ] = useState("student");
  const [ username, setUsername ] = useState('');
  const [ email, setEmail ] = useState('');
  const [ password, setPassword ] = useState('');
  const [ description, setDescription ] = useState('');
  const [ errmsg, setErrMsg ] = useState('');

  const handleSignup = (e) => {
    const signup = async (user, email, pw, desc, r) => {
      try {
        const { message } = await axios.post('http://localhost:8080/api/auth/signup',
          {
            username: user,
            email: email,
            password: pw,
            desc: desc,
            role: [r]
          }
        )

        const { data } = await axios.post('http://localhost:8080/api/auth/signin',
          {
            username: user,
            password: pw
          }
        )

        localStorage.setItem("user", JSON.stringify(data));

        return true;
      } catch (error) {
        setErrMsg(error.message);
        return false;
      }
    }

    e.preventDefault();

    if(signup(username, email, password, description, role)){
      if(role === "student"){
        window.history.pushState({}, '', `/userdash`);
      } else{
        window.history.pushState({}, '', `/clubdash`);
      }

      const navEvent = new PopStateEvent('popstate');
      window.dispatchEvent(navEvent);
    }
  };

  return (
    <ContainerView logoOnly={true}>
    <div className="container">
    <div className="row d-flex flex-row justify-content-center align-items-center" style={{height:'60vh'}}>
        <div className="col-10 col-md-8 col-lg-5">
          <h1 className="my-3" style={{ textAlign:'center' }}>Sign Up</h1>
          <hr />
          {errmsg ?
            <p className="text-danger" style={{ textAlign:'center' }}>{errmsg}</p>
            : null}
          <form onSubmit={handleSignup}>
            <div className="d-flex flex-column align-items-center">
              <h6>Account Type:</h6>
              <span>
                <label className="mr-3">
                  <input
                    type="radio"
                    name="user-type"
                    value="student"
                    className="mr-1"
                    checked={role==="student"}
                    onChange={e => setRole(e.target.value)}
                  />
                  Student
                </label>
                <label>
                  <input
                    type="radio"
                    name="user-type"
                    value="club"
                    className="mr-1"
                    checked={role==="club"}
                    onChange={e => setRole(e.target.value)}
                  />
                  Club
                </label>
              </span>
            </div>
            {role === "student" ?
            <UserFields
              username={username}
              email={email}
              password={password}
              setUsername={setUsername}
              setEmail={setEmail}
              setPassword={setPassword}
            /> :
            <ClubFields
              username={username}
              email={email}
              password={password}
              description={description}
              setUsername={setUsername}
              setPassword={setPassword}
              setEmail={setEmail}
              setDescription={setDescription}
            />}
            <div className="d-flex flex-row justify-content-between align-items-center">
              <Link className="mr-auto ml-1" href="/login">
                <span className="hide-mobile">Already have an account?</span>
                <span className="show-mobile">Sign In</span>
              </Link>
              <button className="btn btn-primary ml-auto mr-1" type="Submit">Submit</button>
            </div>
          </form>
        </div>
      </div>
    </div>
    </ContainerView>
  );
};

export default SignupPage;
